import React from 'react';
import Cookies from 'universal-cookie';

function Edit ({ quiz }) {
  const cookies = new Cookies();
  const [questions, setQuestions] = React.useState(quiz.questions);

  const getInfo = () => {
    fetch('http://localhost:5005/admin/quiz/' + quiz.id, {
      method: 'GET',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
        Authorization: 'Bearer ' + cookies.get('token')
      }
    }).then(data => {
      if (data.status === 200) {
        data.json().then((result) => {
          setQuestions(result.questions).map(question => {
            return <p key={question.description}>{question.description}</p>;
          })
        })
      }
    })
  }

  return (
    <p>{questions} {getInfo}</p>
  );
}

export default Edit;
