import React from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min.js';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect
} from 'react-router-dom';
import Cookies from 'universal-cookie';

const cookies = new Cookies();
const [alertArea, setAlert] = React.useState(null);

export default function App () {
  return (
    <div>
      <div>{alertArea}</div>
      <Navigation />
      <Body />
    </div>
  );
}

function Navigation () {
  return (
    <ul>
      <li>
        <Link to="/dashboard">
          Dashboard
        </Link>
        <Link to="/logout">
          Logout
        </Link>
      </li>
    </ul>
  );
}

function Body () {
  return (
    <Switch>
      <Route path="/dashboard">
        <Dashboard setAlert={setAlert} />
      </Route>
      <Route path="/">
        {cookies.get('token') ? <Redirect to="/dashboard" /> : <Redirect to="/login" />}
      </Route>
    </Switch>
  );
}
