import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min.js';
import Cookies from 'universal-cookie';
import {
  Redirect
} from 'react-router-dom';

export default function Register () {
  const cookies = new Cookies();
  const [redirect, setRedirect] = React.useState('');
  const [email, setEmail] = React.useState('');
  const [pw, setPassword] = React.useState('');
  const [name, setName] = React.useState('');
  const [alertArea, setAlert] = React.useState(null);
  const closeBtn = React.createElement('button', {
    className: 'btn-close',
    onClick: () => { setAlert(null); }
  }, 'Close');

  const checkCredential = (e) => {
    fetch('http://localhost:5005/admin/auth/register', {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        email: email,
        password: pw,
        name: name
      })
    }).then(data => {
      if (data.status === 200) {
        data.json().then((result) => {
          cookies.set('token', result.token);
          setAlert(React.createElement('div', {
            className: 'alert alert-success alert-dismissible fade show',
            role: 'alert'
          }, 'success', closeBtn));
          setRedirect('/dashboard');
        });
      } else {
        data.json().then((result) => {
          setAlert(React.createElement('div', {
            className: 'alert alert-danger alert-dismissible fade show',
            role: 'alert'
          }, result.error, closeBtn));
        });
      }
    });
  };

  if (redirect === '') {
    return (
      <>
        <div>
          {alertArea}
        </div>
        <div
          className='form-signin'
        >
          <div
            className='form-floating'
          >
            <label>
              <input
                type='email'
                className='form-control'
                onChange={(e) => { setEmail(e.target.value); }}
              ></input>
              Email
            </label>
          </div>
          <div
            className='form-floating'
          >
            <label>
              <input
                type='password'
                className='form-control'
                onChange={(e) => { setPassword(e.target.value); }}
              ></input>
              Password
            </label>
          </div>
          <div
            className='form-floating'
          >
            <label>
              <input
                type='name'
                className='form-control'
                onChange={(e) => { setName(e.target.value); }}
              ></input>
              Name
            </label>
          </div>
          <button
            className='btn btn-primary'
            onClick={checkCredential}
          >
            Register
          </button>
          <a href="/login">
            {'Already have an account? Login'}
          </a>
        </div>
      </>
    );
  } else {
    return (
      <Redirect push to={redirect} />
    );
  }
};
