import styled, { css } from 'styled-components';

function BaseButton () {
  const removeDefaultButtonStyle = css`
    border 0;
    padding: 0;
    font-family: inherit;
    font-weight: normal;
    background-color: unset;
  `;

  const BaseButton = styled.button`
    ${removeDefaultButtonStyle};
    box-sizing: border-box;
    cursor: pointer;
    outline: none;
    border-radius: 4px;
    font-weight: bold;
  `;

  return (
    {BaseButton}
  );
}

export default BaseButton;
