import React from 'react';
import { useParams } from 'react-router';
import Cookies from 'universal-cookie';

function CreateQuestion () {
  const cookies = new Cookies();
  const { id } = useParams();
  // const [updateType, setUpdateType] = React.useState('multiple');
  // const [numAnswer, setNumAnswer] = React.useState('1');
  // if (updateType === 'single') {
  //   setNumAnswer('1');
  // } else {
  //   setNumAnswer('1');
  // }
  // const updateValue = (e) => {
  //   setUpdateType(e.target.value)
  // }
  // console.log(numAnswer)

  const createQ = () => {
    fetch('http://localhost:5005/admin/quiz/' + id, {
      method: 'PUT',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
        Authorization: 'Bearer ' + cookies.get('token')
      },
      body: JSON.stringify({
        questions: upload.questions.map((q) => {
          return {
            id: q.id,
            description: q.description,
            time: q.time,
            point: q.point,
            src: q.src ? q.src : null,
            choices: q.choices.map((c) => {
              return {
                id: c.id,
                choice: c.choice
              };
            }),
            answers: q.answers
          };
        }),
        name: upload.name,
        thumbnail: upload.thumbnail ? upload.thumbnail : null
      })
    }).then(data => {
      if (data.status === 200) {
        changeAlert('success', 'You have created "' + name + '"');
        setRedirect('/dashboard');
      } else if (data.status === 403) {
        forceLogout();
        setRedirect('/login');
      } else {
        data.json().then((result) => {
          changeAlert('danger', result.error);
        });
      }
    });
  }

  return (
    <body
    className="text-center"
    >
      <div>
      <h1
      className="firstsection">
        Create a new question
      </h1>
      <br />
      <div
      className='form-floating'
      >
        <label>
          Question:
        </label>
        <form
        className='form-control'
        placeholder='Type'
        >
          <label>
            Type:
          </label>
          <select
          >
            <option
            value="muliple"
            >
              Multiple choices
            </option>
            <option
            value="single"
            >
              Single choices
            </option>
          </select>
        </form>
      </div>
      <div
      className='form-floating'
      >
        <label
        className="sr-only"
        >
          Questions
        </label>
        <input
        className='form-control'
        placeholder='Questions'
        >
        </input>
        <label
        className="sr-only"
        >
          Time limit (seconds)
        </label>
        <input
        className='form-control'
        placeholder='Time limit (seconds)'
        >
        </input>
        <label
        className="sr-only">
          Points
        </label>
        <input
        className='form-control'
        placeholder='Points'
        >
        </input>
        <label
        className="sr-only">
          URL
        </label>
        <input
        className='form-control'
        placeholder='URL'
        >
        </input>
      </div>
      <br />
      <label>
        Answers:
      </label>
      <div className='form-control'>
      <input type='checkbox'></input>
      <input
      placeholder='Answer'
      >
      </input>
      </div>
      <div className='form-control'>
      <input type='checkbox'></input>
      <input
      placeholder='Answer'
      >
      </input>
      </div>
      <div className='form-control'>
      <input type='checkbox'></input>
      <input
      placeholder='Answer'
      >
      </input>
      </div>
      <div className='form-control'>
      <input type='checkbox'></input>
      <input
      placeholder='Answer'
      >
      </input>
      </div>
      <div className='form-control'>
      <input type='checkbox'></input>
      <input
      placeholder='Answer'
      >
      </input>
      </div>
      <div className='form-control'>
      <input type='checkbox'></input>
      <input
      placeholder='Answer'
      >
      </input>
      </div>
      <br />
      <button
      className='btn btn-lg btn-primary btn-block btn-sm'
      onClick={createQ}
      >
      Update
      </button>
      <br />
      </div>
    </body>
  );
}

export default CreateQuestion;
