import styled from 'styled-components';

export const removeDefaultButtonStyle = css`
  border 0;
  padding: 0;
  font-family: inherit;
  font-weight: normal;
  background-color: unset;
`;

export const BaseButton = styled.button`
  ${removeDefaultButtonStyle};
  box-sizing: border-box;
  cursor: pointer;
  outline: none;
  border-radius: 4px;
  font-weight: bold;
`

function createHoverableButton(backgroundColor, hoverColor, inverted){
  return styled(BaseButton)`
    color: ${inverted ? 'white' : 'black'}; 
    background-color: ${backgroundColor};

    &:hover{
        background-color: ${hoverColor};
    }
  `;
}

export const PrimaryButton = createHoverableButton(
  '#19aaee',
  '#33bbff',
  'true'
);

export const ContrastButton = createHoverableButton(
  "#7d2ae8",
  "#8d39fa",
  "true"
);
