import React from 'react';
import propTypes from 'prop-types';
import Cookies from 'universal-cookie';
import {
  Redirect
} from 'react-router-dom';

function Login ({ setAlert }) {
  const cookies = new Cookies();
  const [redirect, setRedirect] = React.useState('');
  const [email, setEmail] = React.useState('');
  const [pw, setPassword] = React.useState('');
  const closeBtn = React.createElement('button', {
    className: 'btn-close',
    onClick: () => { setAlert(null); }
  }, 'x');

  const checkCredential = (e) => {
    fetch('http://localhost:5005/admin/auth/login', {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        email: email,
        password: pw
      })
    }).then(data => {
      if (data.status === 200) {
        data.json().then((result) => {
          cookies.set('email', email);
          cookies.set('token', result.token);
          setAlert(React.createElement('div', {
            className: 'alert alert-success alert-dismissible fade show',
            role: 'alert'
          }, 'You have logged in.', closeBtn));
          setRedirect('/dashboard');
        });
      } else {
        data.json().then((result) => {
          setAlert(React.createElement('div', {
            className: 'alert alert-danger alert-dismissible fade show',
            role: 'alert'
          }, result.error, closeBtn));
        });
      }
    });
  };

  if (redirect === '') {
    return (
      <body className="text-center">
        <div
          className='form-signin'
        >
          <div
            className='form-floating'
          >
            <label>
              <input
                type='email'
                className='form-control'
                onChange={(e) => { setEmail(e.target.value); }}
              ></input>
              Email
            </label>
          </div>
          <div
            className='form-floating'
          >
            <label>
              <input
                type='password'
                className='form-control'
                onChange={(e) => { setPassword(e.target.value); }}
              ></input>
              Password
            </label>
          </div>
          <button
            className='btn btn-primary'
            onClick={checkCredential}
          >
            Login
          </button>
          <a href="/register">
            {"Don't have an account? Sign Up"}
          </a>
        </div>
      </body>
    );
  } else {
    return (
      <Redirect push to={redirect} />
    );
  }
}

Login.propTypes = {
  setAlert: propTypes.func
};

export default Login;
