import React from 'react';
import propTypes from 'prop-types';
import Cookies from 'universal-cookie';
import {
  Link,
  Redirect
} from 'react-router-dom';

import GameBoard from './../elements/gameBoard';

function Dashboard ({ setAlert }) {
  const cookies = new Cookies();
  const [redirect, setRedirect] = React.useState('');
  const [gameDashboard, setGames] = React.useState([]);
  const closeBtn = React.createElement('button', {
    className: 'btn-close',
    onClick: () => { setAlert(null); }
  }, 'Close');

  fetch('http://localhost:5005/admin/quiz', {
    method: 'GET',
    headers: {
      Accept: 'application/json',
      'Content-Type': 'application/json',
      Authorization: 'Bearer ' + cookies.get('token')
    }
  }).then(data => {
    if (data.status === 200) {
      data.json().then((result) => {
        setGames(result.quizzes.map((quiz) => {
          return <GameBoard key={quiz.id} quiz={quiz} />
        }));
      });
    } else {
      cookies.remove('email');
      cookies.remove('token');
      setAlert(React.createElement('div', {
        className: 'alert alert-warning alert-dismissible fade show',
        role: 'alert'
      }, 'Your session has expired. Please log in again.', closeBtn));
      setRedirect('/login');
    }
  });

  if (redirect === '') {
    return (
      <>
        <div>
          <Link
            to='create'
          >
            <button>Create new game</button>
          </Link>
          {gameDashboard}
        </div>
      </>
    );
  } else {
    return (
      <Redirect push to={redirect} />
    );
  }
}

Dashboard.propTypes = {
  setAlert: propTypes.func
};

export default Dashboard;
