import React from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min.js';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect
} from 'react-router-dom';
import Cookies from 'universal-cookie';

import Login from './pages/login';
import Register from './pages/register';
import Dashboard from './pages/dashboard';
import Create from './pages/create';
import Join from './pages/join';
import Play from './pages/play';
import Logout from './pages/logout';
import Edit from './elements/editGameBorad';

function Navigation () {
  return (
    <nav>
      <ul>
        <li>
          <Link to="/logout">
            Logout
          </Link>
        </li>
      </ul>
    </nav>
  );
}
function App () {
  const cookies = new Cookies();

  const [alertArea, setAlert] = React.useState(null);
  const closeBtn = React.createElement('button', {
    className: 'btn-close',
    onClick: () => { setAlert(null); }
  }, 'Close');
  const changeAlert = (type, message) => {
    setAlert(React.createElement('div', {
      className: 'alert alert-' + type + ' alert-dismissible fade show',
      role: 'alert'
    }, message, closeBtn));
  };
  const forceLogout = () => {
    cookies.remove('email');
    cookies.remove('token');
    changeAlert('warning', 'Your session has expired. Please log in again.');
  };

  return (
    <Router>
      <div>
        {alertArea}
      </div>
      <Switch>
        <Route path="/login">
          <Login changeAlert={changeAlert} />
        </Route>
        <Route path="/register">
          <Register changeAlert={changeAlert} />
        </Route>
        <Route path="/dashboard">
          <Navigation />
          <Dashboard
            changeAlert={changeAlert}
            forceLogout={forceLogout}
          />
        </Route>
        <Route path="/create">
          <Navigation />
          <Create
            changeAlert={changeAlert}
            forceLogout={forceLogout}
          />
        </Route>
        <Route path="/logout">
          <Logout changeAlert={changeAlert} />
        </Route>
        <Route path="/edit/:id">
          <Edit changeAlert={changeAlert} />
        </Route>
        <Route path="/join/:id">
          <Join changeAlert={changeAlert} />
        </Route>
        <Route exact path="/join">
          <Join changeAlert={changeAlert} />
        </Route>
        <Route path="/play/:id">
          <Play changeAlert={changeAlert} />
        </Route>
        <Route exact path="/">
          {cookies.get('token') ? <Redirect to="/dashboard" /> : <Redirect to="/login" />}
        </Route>
        <Route path="/">
          Sorry, I could not find that page
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
