import React from 'react';
import propTypes from 'prop-types';
import Cookies from 'universal-cookie';
import {
  Link
} from 'react-router-dom';
function GameBoard ({ quiz, changeAlert, forceLogout }) {
  const cookies = new Cookies();
  const [show, setShow] = React.useState(true);
  const [name, setName] = React.useState(quiz.name);
  const [questions, setQuestions] = React.useState(quiz.questions);
  const [thumbnail, setThumbnail] = React.useState(quiz.thumbnail);
  const [nQuestions, setNQuestions] = React.useState(0);
  const [time, setTime] = React.useState(0);
  const [session, setSession] = React.useState(quiz.active);
  const [hasSession, setHasSession] = React.useState(quiz.active !== null);
  const initialisation = React.useRef(true);
  const [popupArea, setPopup] = React.useState(null);

  const closeBtn = React.createElement('button', {
    onClick: () => { setPopup(null); }
  }, 'Close');

  const getInfo = () => {
    fetch('http://localhost:5005/admin/quiz/' + quiz.id, {
      method: 'GET',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
        Authorization: 'Bearer ' + cookies.get('token')
      }
    }).then(data => {
      if (data.status === 200) {
        data.json().then((result) => {
          setName(result.name);
          setNQuestions(result.questions.length);
          setQuestions(result.questions.map(que => {
            return <p key={que.description}>{que.description}</p>;
          }));
          setThumbnail(result.thumbnail);
          let timeTmp = 0;
          result.questions.forEach((q) => {
            timeTmp += q.time;
          });
          setTime(timeTmp);
          if (!hasSession && result.active) {
            setSession(result.active);
            setHasSession(true);
          } else if (hasSession && !result.active) {
            setHasSession(false);
          }
        });
      } else if (data.status === 403) {
        forceLogout();
      } else {
        data.json().then((result) => {
          changeAlert('danger', result.error);
        });
      }
    });
  };
  const startSession = () => {
    fetch('http://localhost:5005/admin/quiz/' + quiz.id + '/start', {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
        Authorization: 'Bearer ' + cookies.get('token')
      }
    }).then(data => {
      if (data.status === 200) {
        getInfo();
      } else if (data.status === 403) {
        forceLogout();
      } else {
        data.json().then((result) => {
          changeAlert('danger', result.error);
        });
      }
    });
  };
  const stopSession = () => {
    fetch('http://localhost:5005/admin/quiz/' + quiz.id + '/end', {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
        Authorization: 'Bearer ' + cookies.get('token')
      }
    }).then(data => {
      if (data.status === 200) {
        getInfo();
      } else if (data.status === 403) {
        forceLogout();
      } else {
        data.json().then((result) => {
          changeAlert('danger', result.error);
        });
      }
    });
  };
  const deleteGame = () => {
    fetch('http://localhost:5005/admin/quiz/' + quiz.id, {
      method: 'DELETE',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
        Authorization: 'Bearer ' + cookies.get('token')
      }
    }).then(data => {
      if (data.status === 200) {
        data.json().then((result) => {
          changeAlert('success', 'You have deleted "' + name + '"');
          setShow();
        });
      } else if (data.status === 403) {
        forceLogout();
      } else {
        data.json().then((result) => {
          changeAlert('danger', result.error);
        });
      }
    });
  };
  const copySession = () => {
    navigator.clipboard.writeText('http://localhost:3000/join/' + session);
  };

  React.useEffect(() => {
    getInfo(true);
  }, []);
  React.useEffect(() => {
    console.log(initialisation.current);
    if (initialisation.current) {
      initialisation.current = false;
    } else if (hasSession) {
      setPopup(
        <>
          <div
            className="popup"
          >
            <div>
              <p>You have started a session for &quot;{name}&quot;!</p>
              <p>Session ID: {session} <button onClick={copySession}>Copy link</button></p>
            </div>
            {closeBtn}
          </div>
        </>
      );
    } else {
      setPopup(
        <>
          <div
            className={Popup}
          >
            <div>
              <p>You have stopped the session for &quot;{name}&quot;!</p>
              <Link
                to={'/results/' + session}
              >
                <button
                  className = { BaseButton }>
                    See results
                </button>
              </Link>
            </div>
            {closeBtn}
          </div>
        </>
      );
    }
  }, [hasSession]);

  let img = null;
  if (thumbnail) {
    img = <div><img src={thumbnail} /></div>;
  }

  let buttons = null;
  if (hasSession) {
    buttons = (
      <>
        <button
          onClick={stopSession}
        >
          Stop
        </button>
      </>
    );
  } else {
    buttons = (
      <>
        <button
          onClick={startSession}
        >
          Start
        </button>
        <Link
          to={'/edit/' + quiz.id}
        >
          <button
            className = { PrimaryButton }>
            Edit
          </button>
        </Link>
        <button
          onClick={deleteGame}
        >
          Delete
        </button>
      </>
    );
  }

  if (show) {
    return (
      <>
        <ul className="list-group">
          <li className="list-group-item">
            <Link to="./login">
              <h3>{quiz.name}</h3>
            </Link>
            {questions}
            <p>Number of questions: {nQuestions}</p>
            <p>Time: {(time / 60).toFixed(1)} mins</p>
            {img}
            {buttons}
            {popupArea}
          </li>
        </ul>
      </>
    );
  } else {
    return null;
  }
}

GameBoard.propTypes = {
  quiz: propTypes.object,
  changeAlert: propTypes.func,
  forceLogout: propTypes.func
};

export default GameBoard;
