import React from 'react';
import propTypes from 'prop-types';
import Cookies from 'universal-cookie';
import {
  Redirect
} from 'react-router-dom';

function Register ({ setAlert }) {
  const cookies = new Cookies();
  const [redirect, setRedirect] = React.useState('');
  const [email, setEmail] = React.useState('');
  const [pw, setPassword] = React.useState('');
  const [name, setName] = React.useState('');
  const closeBtn = React.createElement('button', {
    className: 'btn-close',
    onClick: () => { setAlert(null); }
  }, 'Close');

  const checkCredential = (e) => {
    fetch('http://localhost:5005/admin/auth/register', {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        email: email,
        password: pw,
        name: name
      })
    }).then(data => {
      if (data.status === 200) {
        data.json().then((result) => {
          cookies.set('email', email);
          cookies.set('token', result.token);
          setAlert(React.createElement('div', {
            className: 'alert alert-success alert-dismissible fade show',
            role: 'alert'
          }, 'You have registered.', closeBtn));
          setRedirect('/dashboard');
        });
      } else {
        data.json().then((result) => {
          setAlert(React.createElement('div', {
            className: 'alert alert-danger alert-dismissible fade show',
            role: 'alert'
          }, result.error, closeBtn));
        });
      }
    });
  };

  if (redirect === '') {
    return (
      <body className="text-center">
        <div
          className='form-signin'
        >
          <h1 className="h3 mb-3 font-weight-normal">
            Please sign up
          </h1>
          <div
            className='form-floating'
          >
            <label className="sr-only">
              Email
            </label>
              <input
                type='email'
                className='form-control'
                placeholder='Email address'
                onChange={(e) => { setEmail(e.target.value); }}
            ></input>
          </div>
          <div
            className='form-floating'
          >
            <label className="sr-only">
              Password
            </label>
              <input
                type='password'
                className='form-control'
                placeholder='Password'
                onChange={(e) => { setPassword(e.target.value); }}
              ></input>
          </div>
          <div
            className='form-floating'
          >
            <label className="sr-only">
              Name
            </label>
              <input
                type='name'
                className='form-control'
                placeholder='Name'
                onChange={(e) => { setName(e.target.value); }}
              ></input>
          </div>
          <br />
          <button
            className='btn btn-lg btn-primary btn-block'
            onClick={checkCredential}
          >
            Register
          </button>
          <br />
          <a href="/login">
            {'Already have an account? Login'}
          </a>
        </div>
      </body>
    );
  } else {
    return (
      <Redirect push to={redirect} />
    );
  }
}

Register.propTypes = {
  setAlert: propTypes.func
};

export default Register;
