import React from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min.js';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect
} from 'react-router-dom';
import Cookies from 'universal-cookie';

import Login from './pages/login';
import Register from './pages/register';
import Dashboard from './pages/dashboard';
import Create from './pages/create';
import Logout from './pages/logout';

function Navigation () {
  return (
    <nav>
      <ul>
        <li>
          <Link to="/dashboard">
            Dashboard
          </Link>
        </li>
        <li>
          <Link to="/logout">
            Logout
          </Link>
        </li>
      </ul>
    </nav>
  );
}
function App () {
  const cookies = new Cookies();
  const [alertArea, setAlert] = React.useState(null);

  return (
    <Router>
      <div>
        {alertArea}
      </div>
      <Switch>
        <Route path="/login">
          <Login setAlert={setAlert} />
        </Route>
        <Route path="/register">
          <Register setAlert={setAlert} />
        </Route>
        <Route path="/dashboard">
          <Navigation />
          <Dashboard setAlert={setAlert} />
        </Route>
        <Route path="/create">
          <Create setAlert={setAlert} />
        </Route>
        <Route path="/logout">
          <Logout setAlert={setAlert} />
        </Route>
        <Route exact path="/">
          {cookies.get('token') ? <Redirect to="/dashboard" /> : <Redirect to="/login" />}
        </Route>
        <Route path="/">
          Sorry, I could not find that page
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
