import React from 'react';
import { useParams } from 'react-router';
import Cookies from 'universal-cookie';
import {
  Link
} from 'react-router-dom';
import propTypes from 'prop-types';

function Edit ({ changeAlert }) {
  const cookies = new Cookies();
  const { id } = useParams();
  const [questions, setQuestions] = React.useState(null);
  const [quiz, setQuiz] = React.useState([]);
  const [name, setName] = React.useState('');
  const [thumbnail, setThumbnail] = React.useState('');
  const initialisation = React.useRef(true);

  React.useEffect(() => {
    fetch('http://localhost:5005/admin/quiz/' + id, {
      method: 'GET',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
        Authorization: 'Bearer ' + cookies.get('token')
      }
    }).then(data => {
      if (data.status === 200) {
        data.json().then((result) => {
          setQuiz(result.questions);
          setName(result.name);
          setThumbnail(result.thumbnail);
        })
      }
    })
  }, []);

  const deleteQuestion = (i) => {
    console.log(quiz);
    fetch('http://localhost:5005/admin/quiz/' + id, {
      method: 'PUT',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
        Authorization: 'Bearer ' + cookies.get('token')
      },
      body: JSON.stringify({
        questions: quiz,
        name: name,
        thumbnail: thumbnail
      }),
    }).then(data => {
      if (data.status === 200) {
        data.json().then((result) => {
          changeAlert('success', 'You have deleted "' + name + '"');
        });
      }
    });
  };

  React.useEffect(() => {
    if (initialisation.current) {
      initialisation.current = false;
    } else {
      let i = 0;
      setQuestions(quiz.map(question => {
        i++;
        return (
          <ul
          className="list-group"
          key={question.id}
          >
            <li
              key={question.description}
              className="list-group-item"
            >
              {question.description}
              <span
              >
                <Link
                    to={'/edit/' + id + '/' + question.id}
                  >
                    <button>Edit</button>
                  </Link>
                  <button onClick={() => deleteQuestion(i - 1)}>Delete</button>
              </span>
            </li>
          </ul>
        );
      }))
    }
  }, [quiz]);

  return (
    <div className="firstsection">
      <h1
      >
        Question board
      </h1>
      <Link
        to='/createQuestion'
      >
        <button
        className='btn btn-primary'
        >
          Create a new question
        </button>
      </Link>
      <ul className="list-group">
        <li className="list-group-item">{questions}</li>
      </ul>
    </div>
  );
}

Edit.propTypes = {
  changeAlert: propTypes.func
};

export default Edit;
