import React from 'react';
import propTypes from 'prop-types';
import Cookies from 'universal-cookie';
import {
  Link
} from 'react-router-dom';

function GameBoard ({ quiz }) {
  const cookies = new Cookies();
  const [nQuestions, setNQuestions] = React.useState(0);

  let img = null;
  if (quiz.thumbnail) {
    img = <img src={quiz.thumbnail} />;
  }

  React.useEffect(() => {
    fetch('http://localhost:5005/admin/quiz/' + quiz.id, {
      method: 'GET',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
        Authorization: 'Bearer ' + cookies.get('token')
      }
    }).then(data => {
      if (data.status === 200) {
        data.json().then((result) => {
          setNQuestions(result.questions.length);
        });
      }
    });
  }, []);

  return (
    <>
      <div>
        <h3>{quiz.name}</h3>
        <p>Number of questions: {nQuestions}</p>
        {img}
        <Link
          to={'/edit/' + quiz.id}
        >
          <button>Edit</button>
        </Link>
        <Link
          to={'/delete/' + quiz.id}
        >
          <button>Delete</button>
        </Link>
      </div>
    </>
  );
}

GameBoard.propTypes = {
  key: propTypes.number,
  quiz: propTypes.object
};

export default GameBoard;
