import React from 'react';
import '../App.css'
import propTypes from 'prop-types';
import Cookies from 'universal-cookie';
import {
  Link
} from 'react-router-dom';
import { Button } from '../components/Button.styles';
function GameBoard ({ quiz, changeAlert, forceLogout }) {
  const cookies = new Cookies();
  const [show, setShow] = React.useState(true);
  const [name, setName] = React.useState(quiz.name);
  const [thumbnail, setThumbnail] = React.useState(quiz.thumbnail);
  const [questions, setQuestions] = React.useState(quiz.questions);
  const [nQuestions, setNQuestions] = React.useState(0);
  const [time, setTime] = React.useState(0);
  const [session, setSession] = React.useState(quiz.active);
  const [hasSession, setHasSession] = React.useState(quiz.active !== null);
  const [position, setPosition] = React.useState(-1);
  const initialisation = React.useRef(true);
  const [popupArea, setPopup] = React.useState(null);

  const closeBtn = React.createElement('button', {
    onClick: () => { setPopup(null); }
  }, 'Close');

  const getInfo = () => {
    fetch('http://localhost:5005/admin/quiz/' + quiz.id, {
      method: 'GET',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
        Authorization: 'Bearer ' + cookies.get('token')
      }
    }).then(data => {
      if (data.status === 200) {
        data.json().then((result) => {
          setName(result.name);
          setQuestions(result.questions);
          setNQuestions(result.questions.length);
          setThumbnail(result.thumbnail);
          let timeTmp = 0;
          result.questions.forEach((q) => {
            timeTmp += q.time;
          });
          setTime(timeTmp);
          if (!hasSession && result.active) {
            setSession(result.active);
            setHasSession(true);
          } else if (hasSession && !result.active) {
            setHasSession(false);
          }
        });
      } else if (data.status === 403) {
        forceLogout();
      } else {
        data.json().then((result) => {
          changeAlert('danger', result.error);
        });
      }
    });
  };
  const startSession = () => {
    fetch('http://localhost:5005/admin/quiz/' + quiz.id + '/start', {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
        Authorization: 'Bearer ' + cookies.get('token')
      }
    }).then(data => {
      if (data.status === 200) {
        getInfo();
      } else if (data.status === 403) {
        forceLogout();
      } else {
        data.json().then((result) => {
          changeAlert('danger', result.error);
        });
      }
    });
  };
  const stopSession = () => {
    fetch('http://localhost:5005/admin/quiz/' + quiz.id + '/end', {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
        Authorization: 'Bearer ' + cookies.get('token')
      }
    }).then(data => {
      if (data.status === 200) {
        getInfo();
      } else if (data.status === 403) {
        forceLogout();
      } else {
        data.json().then((result) => {
          changeAlert('danger', result.error);
        });
      }
    });
  };
  const deleteGame = () => {
    fetch('http://localhost:5005/admin/quiz/' + quiz.id, {
      method: 'DELETE',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
        Authorization: 'Bearer ' + cookies.get('token')
      }
    }).then(data => {
      if (data.status === 200) {
        data.json().then((result) => {
          changeAlert('success', 'You have deleted "' + name + '"');
          setShow(false);
        });
      } else if (data.status === 403) {
        forceLogout();
      } else {
        data.json().then((result) => {
          changeAlert('danger', result.error);
        });
      }
    });
  };
  const copySession = () => {
    navigator.clipboard.writeText('http://localhost:3000/join/' + session);
  };
  const showShareLink = () => {
    setPopup(
      <>
        <div
          className='popup'
        >
          <div>
            <p>You have started a session for &quot;{name}&quot;!</p>
            <p>Session ID: {session} <button onClick={copySession}>Copy link</button></p>
          </div>
          {closeBtn}
        </div>
      </>
    );
  };
  const updateSessionStatus = () => {
    fetch('http://localhost:5005/admin/session/' + session + '/status', {
      method: 'GET',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
        Authorization: 'Bearer ' + cookies.get('token')
      }
    }).then(data => {
      if (data.status === 200) {
        data.json().then((result) => {
          setQuestions(result.results.questions);
          setPosition(result.results.position);
        });
      } else if (data.status === 403) {
        forceLogout();
      } else {
        data.json().then((result) => {
          changeAlert('danger', result.error);
        });
      }
    });
  };
  const advanceSession = () => {
    fetch('http://localhost:5005/admin/quiz/' + quiz.id + '/advance', {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
        Authorization: 'Bearer ' + cookies.get('token')
      }
    }).then(data => {
      if (data.status === 200) {
        updateSessionStatus();
        console.log(questions[0]);
      } else if (data.status === 403) {
        forceLogout();
      } else {
        data.json().then((result) => {
          changeAlert('danger', result.error);
        });
      }
    });
    updateSessionStatus();
  };

  React.useEffect(() => {
    getInfo(true);
  }, []);
  React.useEffect(() => {
    if (session) {
      updateSessionStatus();
    }
    if (initialisation.current) {
      initialisation.current = false;
    } else if (hasSession) {
      showShareLink();
    } else {
      setPopup(
        <>
          <div
            className='popup'
          >
            <div>
              <p>You have stopped the session for &quot;{name}&quot;!</p>
              <Link
                to={'/results/' + session}
              >
                <button>See results</button>
              </Link>
            </div>
            {closeBtn}
          </div>
        </>
      );
    }
  }, [hasSession]);

  let img = null;
  if (thumbnail) {
    img = <div><img src={thumbnail} /></div>;
  }

  let buttons = null;
  let statusArea = null;
  if (hasSession) {
    if (position < 0) {
      statusArea = (
        <>
          <div>
            The game is not started.
            <button onClick={advanceSession}>Start</button>
          </div>
        </>
      );
    }
    buttons = (
      <>
        {statusArea}
        <div>
          <button
            onClick={() => { showShareLink() }}
          >
            Share link
          </button>
          <button
            onClick={stopSession}
          >
            Stop session
          </button>
        </div>
      </>
    );
  } else {
    buttons = (
      <>
        <button
          onClick={startSession}
        >
          Create session
        </button>
        <Link
          to={'/edit/' + quiz.id}
        >
          <BaseButton>Edit</BaseButton>
        </Link>
        <button
          onClick={deleteGame}
        >
          Delete
        </button>
      </>
    );
  }

  if (show) {
    return (
      <>
        <ul className="list-group">
          <li className="list-group-item">
            <h3>{quiz.name}</h3>
            <p>Number of questions: {nQuestions}</p>
            <p>Time: {(time / 60).toFixed(1)} mins</p>
            {img}
            {buttons}
            {popupArea}
          </li>
        </ul>
      </>
    );
  } else {
    return null;
  }
}

GameBoard.propTypes = {
  quiz: propTypes.object,
  changeAlert: propTypes.func,
  forceLogout: propTypes.func
};

export default GameBoard;
