import styled, { css } from 'styled-components';

function BaseButton () {
  return (
    `
    color: ${inverted ? 'white' : 'black'}; 
    background-color: ${backgroundColor};

    &:hover{
        background-color: ${hoverColor};
    }
  `
  );
}

export default BaseButton;
